const countDownDate=new Date("Jan 25, 2021 00:00:00");function updateTimer(){setInterval(function(){const e=(new Date).getTime(),n=countDownDate-e,t=Math.floor(n/864e5),o=Math.floor(n%864e5/36e5),r=Math.floor(n%36e5/6e4),u=Math.floor(n%6e4/1e3);document.querySelector("#days").innerHTML=t,document.querySelector("#hours").innerHTML=o,document.querySelector("#minutes").innerHTML=r,document.querySelector("#seconds").innerHTML=u},1e3)}updateTimer();