const countDownDate=new Date("Jan 25, 2021 00:00:00");function updateTimer(){setInterval(function(){const e=(new Date).getTime(),t=countDownDate-e,n=Math.floor(t/864e5),o=Math.floor(t%864e5/36e5),r=Math.floor(t%36e5/6e4);document.querySelector("#days").innerHTML=n,document.querySelector("#hours").innerHTML=o,document.querySelector("#minutes").innerHTML=r},1e3)}updateTimer();